<?php
 class ConfigManagement extends Controller{ function __construct($db,$domain,$config) { $this->db = $db; $this->domain = $domain; $this->config = $config; } private function configEncrypt(){ $config_array = (array)$this->config; return array( "config" => base64_encode(json_encode($config_array)) ); } public function getConfig(){ return $this->configEncrypt(); } } 